$(function(){$('a[href^="#"]').click(function(){var t=$(this).attr("href"),c=$("#"==t||""==t?"html":t).offset().top;return $("html, body").animate({scrollTop:c},800,"swing"),!1})}),$(window).scroll(function(){500<$(this).scrollTop()?$("#entry-btn").css("opacity","1"):$("#entry-btn").css("opacity","0"),500<$(this).scrollTop()?$("#contact-btn").css("opacity","1"):$("#contact-btn").css("opacity","0")});